document.addEventListener("DOMContentLoaded", function () {

  //  SWHITCH LOCALISATION  SWHITCH LOCALISATION  SWHITCH LOCALISATION

  var swhithLang = document.querySelectorAll(
    ".langCheck__item:not(.selected) input"
  );
  swhithLang.forEach(function (item) {
    item.addEventListener("change", function () {
      var val = item.name;
      location.reload();
      document.cookie = `content_lang=${val}; max-age=3600`;
    });
  });

  //  SWHITCH LOCALISATION  SWHITCH LOCALISATION  SWHITCH LOCALISATION
  const modalBtn = document.querySelectorAll("[data-modal]"),
    modal = document.querySelectorAll(".modal"),
    body = document.querySelector("body");

  function closeModal(e) {
    let o = e.querySelector(".popup__wrapper");
    o.removeAttribute("style"), body.classList.remove("no-scroll"), setTimeout(function () {
      e.classList.remove("popup-show")
    }, 300)
  }
  modalBtn.forEach(e => {
    e.addEventListener("click", e => {
      let o = e.currentTarget;
      e = o.getAttribute("data-modal");
      let t = document.getElementById(e),
        l = t.querySelector(".popup__wrapper");
      l.addEventListener("click", e => {
        e.stopPropagation()
      }), t.classList.add("popup-show"), body.classList.add("no-scroll"), setTimeout(function () {
        l.style.transform = "none"
      }, 1)
    })
  }), modal.forEach(e => {
    e.addEventListener("click", e => {
      closeModal(e.currentTarget)
    })
  });
  let popupClose = document.querySelector(".popup__close"),
    popup = document.querySelector("#popup-modal");
  popupClose.addEventListener("click", function () {
    popup.classList.remove("popup-show"), body.classList.remove("no-scroll")
  });
});